import axios from 'axios'

const api = axios.create({
    baseURL: 'http://localhost:9889/api/v1/',
})

export const insertProduct = (token, payload) => api.post(`/product`, payload/*, {headers: { Authorization: token}}*/)
export const getAllProducts = (token) => api.get(`/product`/*, {headers: { Authorization: token}}*/)
export const updateProductById = (id, payload, token) => api.put(`/product/${id}`, payload/*, {headers: { Authorization: token}}*/)
export const deleteProductById = (id, token) => api.delete(`/product/${id}`/*, {headers: { Authorization: token}}*/)
export const getProductById = (id, token) => api.get(`/product/${id}`/*, {headers: { Authorization: token}}*/)

const apis = {
    insertProduct,
    getAllProducts,
    updateProductById,
    deleteProductById,
    getProductById,
}

export default apis
